memberdef.initializer:
do {                                                                                \
            if (unlikely(!(X))) {                                                           \
                debug_print_assert(0, #X, NULL, SHORT_FILE, __LINE__, __PRETTY_FUNCTION__); \
            }                                                                               \
        } while(1 == 0)

do {                                                                               \
            if (unlikely(!(X))) {                                                          \
                debug_print_assert(0, #X, STR, SHORT_FILE, __LINE__, __PRETTY_FUNCTION__); \
                AOS_ASSERTION_FAIL_ACTION();                                               \
            }                                                                              \
        } while(1 == 0)

do {                                                                                  \
            if (unlikely(!(X))) {                                                             \
                debug_print_assert( 0, #X, NULL, SHORT_FILE, __LINE__, __PRETTY_FUNCTION__ ); \
                goto LABEL;                                                                   \
            }                                                                                 \
        } while(1 == 0)

do {                                                                               \
            if (unlikely(!(X))) {                                                          \
                debug_print_assert(0, #X, STR, SHORT_FILE, __LINE__, __PRETTY_FUNCTION__); \
                goto LABEL;                                                                \
            }                                                                              \
        } while(1 == 0)

do {                                           \
            if (unlikely((ERR) != 0)) {                \
                { ACTION; }                            \
                goto LABEL;                            \
            }                                          \
        } while(1 == 0)
